var angular = require('angular');
var amodule = angular.module('myApp',[]);

amodule.service('myService', function() {
	return {
		name: 'Santosh',
		URL: 'http://www.angularjs.org',
		country: 'UK',
		getName: function() { return this.name; },
		getURL: function() { return this.URL; }
	};
});

var myappinj = angular.injector(['myApp']);
var myservice = myappinj.get('myService');
var myname = myservice.getName();

console.log(myname);

describe('angularjs homepage', function() {
  it('should greet the named user', function() {
    browser.get(myservice.getURL());
    element(by.model('yourName')).sendKeys('Julie');
    var greeting = element(by.binding('yourName'));
	expect(greeting.getText()).toEqual('Hello Julie!');
  });
});